import React, { useEffect, useState} from 'react';
// import logo from './logo.svg';
import './App.css';
import RecepieComponent from './recepei'

const App = () => {
  const app_id = 'b4ec0dd4';
  const app_key = '4508936520382f8a4ebfa858aaeb7461'
  const urlToHit = `https://api.edamam.com/search?q=chicken&app_id=${app_id}&app_key=${app_key}&from=0&to=3&calories=591-722&health=alcohol-free`;
  const [recepies , setRecepies ] = useState([])
  useEffect(  () => {
     getRecepie()
  }, [])
  const getRecepie = async () => {
    const response = await fetch(urlToHit)
    const recepieData = await response.json()
    setRecepies(recepieData.hits)
    console.log('recepieData', recepieData)
  }

  return (
    <div className="App">
      <form className='search-form'>
        <input type="text" className='search-bar' />
        <button type="submit" className='search-button' > Search </button>
      </form>
      {recepies.map(recepie => (
          <RecepieComponent
              title={recepie.recep}
          />
      ))}
    </div>
  );
}

export default App;
