import React, { useEffect, useState} from 'react';
// import logo from './logo.svg';
import './App.css';
import RecepieComponent from './recepei'

const App = () => {
  const app_id = 'b4ec0dd4';
  const app_key = '4508936520382f8a4ebfa858aaeb7461'
  const [recepies , setRecepies ] = useState([])
  const [search , setSearch ] = useState('')
  const [query , setQuery ] = useState('chicken')
  const urlToHit = `https://api.edamam.com/search?q=${query}&app_id=${app_id}&app_key=${app_key}&from=0&to=3&calories=591-722&health=alcohol-free`;

  const setSearchValue = e => {
    setSearch(e.target.value)
  }

  useEffect(  () => {
     getRecepie()
  }, [])
  const getRecepie = async () => {
    const response = await fetch(urlToHit)
    const recepieData = await response.json()
    setRecepies(recepieData.hits)
  }

  const setQueryValue = e => {
    e.preventDefault()
    setQuery(search)
  }

  return (
    <div className="App">
      <form onSubmit={setQueryValue} className='search-form'>
        <input type="text" className='search-bar' value={search} onChange={setSearchValue}/>
        <button type="submit" className='search-button' > Search </button>
      </form>
      {recepies.map(recepie => (
          <RecepieComponent
              key={recepie.recipe.label}
              title={recepie.recipe.label}
              calories={recepie.recipe.calories}
              image={recepie.recipe.image}
          />
      ))}
    </div>
  );
}

export default App;
